val spark = SparkSession
   .builder()
   .appName("BDRAssignment")
   .getOrCreate()

var profiles = spark.read.format("csv").option("header", "true").option("inferSchema", "true").load("notebooks/profile_data.csv")
var clicks_train = spark.read.format("csv").option("header", "true").option("inferSchema", "true").load("notebooks/click_data_train.csv")
var clicks_val = spark.read.format("csv").option("header", "true").option("inferSchema", "true").load("notebooks/click_data_test.csv")
var zipcode_distances = spark.read.format("csv").option("header", "true").option("inferSchema", "true").load("notebooks/zipcode_distances.csv")
var vacancies_val = spark.read.format("csv").option("header", "true").option("inferSchema", "true").load("notebooks/vacancies_test.csv")

import org.apache.spark.sql.functions._

//hint: make a UDF that you can apply on certain column in dataframe

def replace_column_values(a:Int) : Int = {
  if(a == 1)
    return 2;
  else if(a == 3||a == 5){
    return 4;
  }
  return a;
}

val udf_rep_col_vals = udf(replace_column_values _)


val clicks_val_mod = clicks_val.withColumn("ecom_action", udf_rep_col_vals($"ecom_action"))

clicks_val_mod

